import {VStack, ButtonGroup, FormControl, FormLabel, Button, FormErrorMessage, Input, Heading} from "@chakra-ui/react"
import { useFormik } from "formik";
import * as Yup from "yup";

const Login = () => {
    const formik = useFormik({
        initialValues: {username: "", password: ""},
        validationSchema: Yup.object({
            username: Yup.string().required("Username required.").min(6, "Username too short.").max(28, "Username too long."),
            password: Yup.string().required("Password required.").min(6, "Password too short.").max(28, "Password too long."),
        }),
        onSubmit: (values, actions) => {
            alert(JSON.stringify(values, null, 2));
            actions.resetForm();
        },
    });
  return (
    <div>
        <VStack as="form" w={{base: "90%", md: "500px"}} m="auto" justify="center" h="100vh" spacing="1rem" onSubmit={formik.handleSubmit}>

        <Heading>
            Log In
        </Heading>
        <FormControl pt="1rem">
            <FormLabel fontSize="lg">Username</FormLabel>
            <Input name="username" typeof="text" placeholder="Enter Username" autoComplete="off" size="lg" value={formik.username} onChange={formik.handleChange} onBlur={formik.handleBlur}/>
            <FormErrorMessage>Invalid Username</FormErrorMessage>
        </FormControl>        

        <FormControl>
            <FormLabel fontSize="lg">Password</FormLabel>
            <Input name="password" placeholder="Enter Password" autoComplete="off" size="lg" value={formik.password} onChange={formik.handleChange} onBlur={formik.handleBlur}/>
            <FormErrorMessage>Invalid Password</FormErrorMessage>
        </FormControl>

            <ButtonGroup pt="1rem">
                <Button colorScheme="teal" type="submit">Login</Button>
                <Button>Create Account</Button>
            </ButtonGroup>
        </VStack>
    </div>
  )
}

export default Login
